<template>
  <div class="container">
    <div
      class="title"
    >
      <el-button>本月</el-button>
      <el-button>季度</el-button>
      <el-button >年度</el-button>
      <el-button >自定义</el-button>
    </div>
    <div class="header">
      <div class="header_left">
        <div style=" width: 100%;margin: 10px 0 0 10px">
          <h1 style="display: flex;align-items: center"><span style="color: #246EDB;font-size: 1.5em">·&nbsp;</span>任务状态统计</h1>
        </div>
        <div style="margin-top: 70px; display: flex;justify-content: space-evenly;align-items: center">
          <div>
            <el-progress type="dashboard" width="150" stroke-width="15" :percentage="percentage" color="#4cc1fe"></el-progress>
            <div style="justify-content: center;display: flex;margin-top: -25pt;color: #4cc1fe">执行中</div>
          </div>
          <div>
            <el-progress type="dashboard" width="150" stroke-width="15" percentage="70" color=#47f9d1></el-progress>
            <div style="justify-content: center;display: flex;margin-top: -25pt;color: #47f9d1">已完结</div>
          </div>
          <div>
            <el-progress type="dashboard" width="150" stroke-width="15" percentage="60" color="#fbf649"></el-progress>
            <div style="justify-content: center;display: flex;margin-top: -25pt;color:#fbf649 ">逾期任务</div>
          </div>
        </div>
      </div>
      <div class="header_right">
        <div style=" width: 100%;margin: 10px 0 0 10px">
          <h1 style="display: flex;align-items: center"><span style="color: #246EDB;font-size: 1.5em">·&nbsp;</span>问题类型统计</h1>
        </div>
        <div id="main" style="width: 100%;height:90%;" ></div>
      </div>
    </div>
    <div class="bottom">
      <div >
        <el-table
          :data="conList"
          border
          style="border-radius: 15px;"
          :cell-style="tableRowStyle"
          :header-cell-style="tableHeaderStyle"
          v-loading="appLoading"
          element-loading-text="加载中"
          element-loading-spinner="el-icon-loading"
          element-loading-background="rgba(0, 0, 0, 0.8)"
        >
          <el-table-column prop="" label="问题单位名称">
          </el-table-column>
          <el-table-column prop="" label="纪律作风"></el-table-column>
          <el-table-column prop="" label="内务管理"></el-table-column>
          <el-table-column prop="" label="巡逻执勤"></el-table-column>
          <el-table-column prop="" label="其他类型"></el-table-column>
          <el-table-column prop="" label="任务总数"></el-table-column>
        </el-table>
      </div>
      <div class="page">
        <el-pagination
          @current-change="appListChange"
          :page-sizes="[10]"
          :page-size="10"
          layout="sizes, prev, pager, next"
          :total="appTotal"
        >
        </el-pagination>
      </div>
    </div>
  </div>
</template>

<script>
import axios from 'axios'
import * as echarts from 'echarts'
export default {
  name: 'supervisionContent',
  data () {
    return {
      qiehuan: '本月',
      appLoading: false,
      percentage: 80,
      conList: {
      },
      appTotal: 0
    }
  },
  mounted () {
    this.getContentList()
    this.image()
  },
  methods: {
    // 表格样式
    tableRowStyle ({ row, rowIndex }) {
      if (rowIndex % 2 === 1) {
        return 'background-color: #F7F7FF;text-align:center'
      } else {
        return 'text-align:center'
      }
    },
    // 表格第一行的设置
    tableHeaderStyle ({ row, rowIndex, columnIndex }) {
      if (rowIndex === 0) {
        return 'height: 3.75rem;font-size: 1rem;font-family: Microsoft YaHei;font-weight: bold;line-height: 1.3125rem;color: #333333;text-align:center;background:#ebf5ff'
      }
    },
    async getContentList () {
      const accessToken = window.localStorage.getItem('access_token')
      const Token = window.localStorage.getItem('Token')
      await axios.get('/auth/supervise/task/index', {
        params: {
          page: 1,
          pageSize: 20
        },
        headers: {
          Authorization: `Bearer ${Token}`,
          'x-api-header': 'yuanxibing',
          'x-access-token': accessToken
        }
      }).then(res => {
        this.conList = res.data.data.items
        console.log(res)
      })
    },
    // 分页
    async appListChange () {

    },
    image () {
      // 获取 DOM 元素
      var myChart = echarts.init(document.getElementById('main'))
      // 初始化图表
      var img = 'data:image/png;base64,'

      var trafficWay = [{
        name: '纪律作风',
        value: 20
      }, {
        name: '内务管理',
        value: 10
      }, {
        name: '巡逻执勤',
        value: 30
      }, {
        name: '其他类型',
        value: 40
      }]

      var data = []
      var color = ['#00ffff', '#00cfff', '#006ced', '#ffe000', '#ffa800', '#ff5b00', '#ff3000']
      for (var i = 0; i < trafficWay.length; i++) {
        data.push({
          value: trafficWay[i].value,
          name: trafficWay[i].name,
          itemStyle: {
            normal: {
              borderWidth: 5,
              shadowBlur: 20,
              borderColor: color[i],
              shadowColor: color[i]
            }
          }
        }, {
          value: 2,
          name: '',
          itemStyle: {
            normal: {
              label: {
                show: false
              },
              labelLine: {
                show: false
              },
              color: 'rgba(0, 0, 0, 0)',
              borderColor: 'rgba(0, 0, 0, 0)',
              borderWidth: 0
            }
          }
        })
      }
      var seriesOption = [{
        name: '',
        type: 'pie',
        clockWise: false,
        radius: [105, 109],
        hoverAnimation: false,
        itemStyle: {
          normal: {
            label: {
              show: true,
              position: 'outside',
              color: '#000',
              formatter: function (params) {
                var percent = 0
                var total = 0
                for (var i = 0; i < trafficWay.length; i++) {
                  total += trafficWay[i].value
                }
                percent = ((params.value / total) * 100).toFixed(0)
                if (params.name !== '') {
                  return params.name + '\n' + '\n' + '占百分比：' + percent + '%'
                } else {
                  return ''
                }
              }
            },
            labelLine: {
              length: 30,
              length2: 100,
              show: true,
              color: '#00ffff'
            }
          }
        },
        data: data
      }]
      const option = {
        backgroundColor: '',
        color: color,
        title: {
          text: '共87845',
          top: '48%',
          textAlign: 'center',
          left: '49%',
          textStyle: {
            color: '#000',
            fontSize: 22,
            fontWeight: '400'
          }
        },
        graphic: {
          elements: [{
            type: 'image',
            z: 3,
            style: {
              image: img,
              width: 178,
              height: 178
            },
            left: 'center',
            top: 'center',
            position: [100, 100]
          }]
        },
        tooltip: {
          show: false
        },
        legend: {
          icon: 'circle',
          orient: 'horizontal',
          // x: 'left',
          data: ['纪律作风', '内务管理', '巡逻执勤', '其他类型'],
          right: 0,
          bottom: 20,
          align: 'right',
          textStyle: {
            color: '#000'
          },
          itemGap: 20
        },
        toolbox: {
          show: false
        },
        series: seriesOption
      }
      // 设置图表配置项
      myChart.setOption(option)
    }
  }
}
</script>

